import React, { useEffect, useState } from 'react';
export const AuthContext = React.createContext({});

const Inicial_State = [
    {
        name: 'Audi',
        url:
          'https://images.pexels.com/photos/1149831/pexels-photo-1149831.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500',
      },
      {
        name: 'Audi',
        url:
          'https://images.pexels.com/photos/1149831/pexels-photo-1149831.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500',
      },
      {
        name: 'Audi',
        url:
          'https://images.pexels.com/photos/1149831/pexels-photo-1149831.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500',
      },
]

export const AuthProvider = (props) => {
    const [cars, setCars] = useState({
        Inicial_State
    })

    useEffect(()=>{
        const userStorage = localStorage.getItem("cars");
        if(userStorage){
            setCars(JSON.parse(userStorage));
        }
    }, [])

    return(
        <AuthContext.Provider value={{cars, setCars}}>
            {props.children}
        </AuthContext.Provider>
    )
}

export const useAuth = ()=>React.useContext(AuthContext);
